import React, { useCallback, useEffect } from "react";
import "./displayQuotes.css";
import { useState } from "react";
import axios from "axios";
import QuoteCard from "./QuoteCard";
import FiltersSection from "./FiltersSection";

export default function DisplayQuotes() {
  const [quotes, setQuotes] = useState([]);
  const [tags, setTags] = useState([]);

  const getQuotes = useCallback(() => {
    axios.get("http://localhost:8000/quotes").then(({ data }) => {
      return data.quotes;
    });
  }, []);

  const getTags = useCallback(() => {
    axios.get("http://localhost:8000/tags").then(({ data }) => {
      return data;
    });
  }, []);

  const updateQuote = (quote) => {
    setQuotes(
      quotes.map((q) => {
        if (q.id === quote.id) {
          return { ...quote };
        }
        return q;
      })
    );
  };

  useEffect(() => {
    (async () => {
      try {
        const [quotesRes, tagsRes] = await Promise.all([
          getQuotes(),
          getTags(),
        ]);
        console.log(quotesRes);
        setQuotes(quotesRes);
        setTags(tagsRes);
      } catch (err) {
        console.log(err);
      }
    })();
  }, [getQuotes, getTags]);

  return (
    <div className="container-display-quotes">
      <FiltersSection />
      {quotes.length ? (
        quotes.map((quote, i) => {
          return (
            <QuoteCard
              key={quote.id + i}
              quote={quote}
              getQuotes={getQuotes}
              updateQuote={updateQuote}
            />
          );
        })
      ) : (
        <h1>No quotes</h1>
      )}
    </div>
  );
}
