import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretUp, faCaretDown } from "@fortawesome/free-solid-svg-icons";
import {
  calculateColorByPercentage,
  calculatePercentageOfVotes,
} from "../helpers";
import "./QuoteCard.css";
import axios from "axios";

const QuoteCard = ({ quote, getQuotes, updateQuote }) => {
  const percentage = calculatePercentageOfVotes(
    quote.upvotesCount,
    quote.downvotesCount
  ).toFixed(0);

  const positiveMark = async () => {
    try {
      const response = await axios.post(
        `http://localhost:8000/quotes/${quote.id}/upvote`
      );
      updateQuote(response.data, "upvote");
    } catch (error) {
      console.log("console.log(error);", error);
    }

    // if (quote.givenVote === "none" && quote.givenVote !== "downvote") {
    //   const idQuote = quote.id;
    //   axios
    //     .post("http://localhost:8000/quotes/" + idQuote + upvote)
    //     .then(({ data }) => {
    //       // setQuotes([])
    //       //     setQuotes([...quote , data])
    //       //   console.log(data)
    //       console.log(data);
    //     })
    //     .catch((err) => {
    //       console.log(err, "Vec glaso");
    //     });
    // }
  };

  return (
    <div className="container-quote">
      <div className="left-container">
        <div className="up-icon">
          <FontAwesomeIcon
            onClick={positiveMark}
            icon={faCaretUp}
            style={quote.givenVote === "upvote" && { color: "lightGreen" }}
          />
        </div>
        <div
          className="percentage"
          style={{ color: calculateColorByPercentage(percentage) }}
        >
          {percentage}%
        </div>
        <div className="mark">
          {quote.upvotesCount} / {quote.downvotesCount}
        </div>
        <div className="down-icon">
          <FontAwesomeIcon
            icon={faCaretDown}
            style={quote.givenVote === "downvote" && { color: "red" }}
          />
        </div>
      </div>
      <div className="right-container">
        <p>{quote.content}</p>
        <h5>-{quote.author}</h5>
      </div>
    </div>
  );
};

export default QuoteCard;
