import React, { memo, useEffect, useState } from "react";
import "./FiltersSection.css";

function FilterBy({ tags, submitHandler, by, multiple = false }) {
  const [isOpen, setIsOpen] = useState(false);

  const [selectedTags, setSelectedTags] = useState([]);

  console.log("TagRendering");

  const checkboxHandler = (tag) => {
    if (!multiple) {
      console.log(tag);
      setSelectedTags([tag]);
      return;
    }

    const index = selectedTags.findIndex((t) => tag === t);
    console.log(index);
    if (index === -1) {
      setSelectedTags((oldTags) => [...oldTags, tag]);
      return;
    }

    setSelectedTags((oldTags) => {
      const newTags = [...oldTags];
      newTags.splice(index, 1);
      return newTags;
    });
  };
  console.log(selectedTags);
  useEffect(() => {
    submitHandler({ [by]: selectedTags });
  }, [selectedTags]);

  return (
    <div className="tags-container">
<div
      style={{
        backgroundColor: "red",
        width: "300px",
        display: "flex",
        flexDirection: "column",
        position: "relative",
      }}
    >
      <div
        style={{ backgroundColor: "blue" }}
        onClick={() => setIsOpen((oldValue) => !oldValue)}
      >
        {by}
      </div>
      {isOpen && (
        <ul
          style={{
            backgroundColor: "green",
            listStyleType: "none",
            position: "absolute",
            width: "100%",
            display: "flex",
            flexDirection: "column",
            padding: "0",
          }}
        >
          {tags?.map((tag) => (
            <li key={tag}>
              <input
                type="checkbox"
                name={tag}
                checked={selectedTags.includes(tag)}
                onChange={() => checkboxHandler(tag)}
              />
              <label htmlFor={tag}>{tag}</label>
            </li>
          ))}
        </ul>
      )}
    </div>
    </div>

  );
}

export default memo(FilterBy);
