import React, { useRef } from "react";
import './FiltersSection.css'

export default function FiltersSection() {

    const checkRef = useRef();


    var expanded = false;

    function showCheckBox () {
        console.log(checkRef)
        if(!expanded){
            checkRef.current.style.display = 'block';
            expanded = true;
        }
        else {
            checkRef.current.style.display = 'none';
            expanded = false;
        }
    }

  return (
    <div>
      <div className="multiselect">
        <div className="selectBox" onClick={showCheckBox}>
            <select>
                <option>Select an option</option>
            </select>
            <div  checkRef={checkRef} id='checkboxes'>

                <label for='one'><input type='checkbox' id='one'/>First checbox</label>
                <label for='two'><input type='checkbox' id='two'/>First checbox</label>
                <label for='three'><input type='checkbox' id='three'/>First checbox</label>

            </div>
        </div>
      </div>
    </div>
  );
}
