import React, { useEffect } from "react";
import "./displayQuotes.css";
import { useState } from "react";
import axios from "axios";
import QuoteCard from "./QuoteCard";

export default function DisplayQuotes() {
  const [quotes, setQuotes] = useState([]);

  const getQuotes = useCallback(() => {
    axios.get("http://localhost:8000/quotes").then(({ data }) => {
      const resQuotes = data.quotes;
      setQuotes(resQuotes);
    });
  }, []);
  const updateQuote = (quote, voteValue) => {
    setQuotes(
      quotes.map((q) => {
        if (q.id === quote.id) {
          return { ...quote };
          // return {
          //   ...q,
          //   givenVote: voteValue,
          //   downvotesCount: quote.downvotesCount,
          //   upvotesCount: quote.upvotesCount,
          // };
        }
        return q;
      })
    );
  };

  useEffect(() => {
    // TODO: extract to function
    // axios.get("http://localhost:8000/quotes").then(({ data }) => {
    //   const resQuotes = data.quotes;
    //   setQuotes(resQuotes);
    // });
    getQuotes();
  }, [quotes]);

  return (
    <div className="container-display-quotes">
      {quotes.length ? (
        quotes.map((quote, i) => {
          console.log(quote);
          return (
            <QuoteCard
              key={quote.id + i}
              quote={quote}
              getQuotes={getQuotes}
              updateQuote={updateQuote}
            />
          );
        })
      ) : (
        <h1>No quotes</h1>
      )}
    </div>
  );
}
