import React, { useEffect } from "react";
import "./displayQuotes.css";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretUp, faCaretDown } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { calculatePercentageOfVotes } from "../helpers";

export default function DisplayQoutes() {


  const [quotes, setQuotes] = useState([{
    downvotesCount: 0,
    upvotesCount: 0,
    content: "null",
    author: "null"


  }])

  useEffect(() => {
    axios.get('http://localhost:8000/quotes')
    .then(({data}) => {

      const resQuotes = data.quotes
      setQuotes(resQuotes)

    })
  }, [])

  console.log(quotes)



  // const [quotes, setQuotes] = useState([
  //   {
  //     id: "4f71adf6-b7f5-45a5-82d6-1ed37d79c2d1",
