  //       "universe",
  //     ],
  //     userId: "d964099a-1a2a-46f5-9782-e2601b5aac9e",
  //     upvotesCount: 26,
  //     downvotesCount: 2,
  //     createdAt: "2020-07-12T07:55:35.090Z",
  //     upvotedBy: [
  //       "d964099a-1a2a-46f5-9782-e2601b5aac9e",
  //       "49d73d43-e1bc-46b4-88a6-d802d1cc9fe9",
  //       "3de3d9ff-60b1-4694-9e87-77aefea9ea0e",
  //     ],
  //     downvotedBy: [],
  //   },
  // ]);
  // console.log(quotes);
  return (
    <div className="container-display-quotes">
      {quotes.map((quotes) => {
        return (
          <div className="container-quotes">
            <div className="left-container">
              <div className="up-icon">
                <FontAwesomeIcon icon={faCaretUp} />
              </div>
              <div className="percentage">
                {calculatePercentageOfVotes(quotes.upvotesCount,quotes.downvotesCount).toFixed(0)} %
              </div>
              <div className="mark">
               {quotes.upvotesCount} / {quotes.downvotesCount}
              </div>
              <div className="down-icon">
                <FontAwesomeIcon icon={faCaretDown} />
              </div>
            </div>
            <div className="right-container">
              <p>{quotes.content}</p>
              <h5>-{quotes.author}</h5>
            </div>
          </div>
        );
      })}
    </div>
  );
}
