import React, { memo, useState } from "react";
import "./FiltersSection.css";

function TagsSelect({ tags, submitHandler }) {
  const [isOpen, setIsOpen] = useState(false);

  const [selectedTags, setSelectedTags] = useState([]);

  console.log("TagRendering");

  const checkboxHandler = (tag) => {
   const index = selectedTags.findIndex((t) => tag === t);
   if(index===-1){
    setSelectedTags(oldTags=>[...oldTags,tag])
    return
   }

  };

  return (
    <div
      style={{
        backgroundColor: "red",
        width: "300px",
        display: "flex",
        flexDirection: "column",
        position: "relative",
      }}
    >
      <div
        style={{ backgroundColor: "blue" }}
        onClick={() => setIsOpen((oldValue) => !oldValue)}
      >
        Filter
      </div>
      {isOpen && (
        <ul
          style={{
            backgroundColor: "green",
            listStyleType: "none",
            position: "absolute",
            width: "100%",
            display: "flex",
            flexDirection: "column",
            padding: "0",
          }}
        >
          {tags?.map((tag) => (
            <li key={tag} onClick={checkboxHandler}>
              <input type="checkbox" name={tag} />
              <label htmlFor={tag}>{tag}</label>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default memo(TagsSelect);
