import axios from "axios";
import { createContext, useState } from "react";
import { getCookie, setCookie } from "../utils";

export const UserContext = createContext();

const UserContextProvider = ({ children }) => {
  const [isLogged, setIsLogged] = useState(false);
  const [token, setToken] = useState("")

  const [user, setUser] = useState({ username: "", password: "" });

   const  login = () => {
        axios.post("http://localhost:8000/sessions",  user ).then(({data}) => {
        const accessToken = data.accessToken
        console.log(accessToken)
        setToken(accessToken)   
        setCookie("accessToken", accessToken, 180);
        setIsLogged(true);
        { window.history.pushState(null, "", "http://localhost:3000/home")}    });


  };

  const getUser = (value, inputType) => {
    setUser({ ...user, [inputType]: value });
  };

  return (
    <UserContext.Provider
      value={{
        user,
        setUser,
        login,
        getUser,
        isLogged,
        setIsLogged,
        token
      }}
    >
      {children}
    </UserContext.Provider>
  );
};

export default UserContextProvider;
