import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretUp, faCaretDown } from "@fortawesome/free-solid-svg-icons";
import {
  calculateColorByPercentage,
  calculatePercentageOfVotes,
  positiveMark,
} from "../helpers";
// import "./displayQuotes.css";
import "./QuoteCard.css";

const QuoteCard = (props) => {
  const { quote } = props;
  const { setQuotes } = props;

  const percentage = calculatePercentageOfVotes(
    quote.upvotesCount,
    quote.downvotesCount
  ).toFixed(0);

  return (
    <div className="container-quote">
      <div className="left-container">
        <div className="up-icon">
          <FontAwesomeIcon
            onClick={() => positiveMark(quote, setQuotes, "upvote")}
            icon={faCaretUp}
          />
        </div>
        <div
          className="percentage"
          style={{ color: calculateColorByPercentage(percentage) }}
        >
          {percentage}%
        </div>
        <div className="mark">
          {quote.upvotesCount} / {quote.downvotesCount}
        </div>
        <div className="down-icon">
          <FontAwesomeIcon icon={faCaretDown} />
        </div>
      </div>
      <div className="right-container">
        <p>{quote.content}</p>
        <h5>-{quote.author}</h5>
      </div>
    </div>
  );
};

export default QuoteCard;
