import React, { useEffect } from "react";
import "./displayQuotes.css";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretUp, faCaretDown } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { calculatePercentageOfVotes } from "../helpers";
import QuoteCard from "./QuoteCard";

export default function DisplayQoutes() {
  const [quotes, setQuotes] = useState([
    {
      downvotesCount: 0,
      upvotesCount: 0,
      content: null,
      author: null,
    },
  ]);

  useEffect(() => {
    axios.get("http://localhost:8000/quotes").then(({ data }) => {
      const resQuotes = data.quotes;
      setQuotes(resQuotes);
    });
  }, []);

  return (
    <div className="container-display-quotes">
      {quotes.map((quote) => {
        return <QuoteCard quote={quote} setQuotes={setQuotes} />;
      })}
    </div>
  );
}
