import React, { useEffect } from "react";
import "./displayQuotes.css";
import { useState } from "react";
import axios from "axios";
import QuoteCard from "./QuoteCard";

export default function DisplayQuotes() {
  const [quotes, setQuotes] = useState([]);

  const voteQuotes = (quote) => {
    setQuotes(
      quotes.map((q) => {
        if (q.id === quote.id) {
          return quote;
        }
        return q;
      })
    );
  };

  useEffect(() => {
    // TODO: extract to function
    // axios.get("http://localhost:8000/quotes").then(({ data }) => {
    //   const resQuotes = data.quotes;
    //   setQuotes(resQuotes);
    // });
  }, []);

  return (
    <div className="container-display-quotes">
      {quotes.length ? (
        quotes.map((quote, i) => {
          return (
            <QuoteCard
              key={quote.id + i}
              quote={quote}
              voteQuotes={voteQuotes}
            />
          );
        })
      ) : (
        <h1>No quotes</h1>
      )}
    </div>
  );
}
