import React, {useState} from 'react'


function FormQuotes() {

    const addQuotes = () => {
        console.log(quotesObj)
    }

    const [quotesObj, setQuotesObj] = useState({
        content: "",
        author: "",
        tags: []
    })

    const handleOnChange = (value, inputType) => {
        setQuotesObj({ ...quotesObj, [inputType]: value });
      };


  return (
    <div className="form-container">
    <form>
    <label>
    content:
    <input onChange={(e) => {handleOnChange(e.target.value , "content")}} type="content" name="content" />
    </label>
    <label>
    author:
    <input onChange={(e) => {handleOnChange(e.target.value , "author")}} type="author" name="author" />
    </label>
    <label>
    tags:
    <input onChange={(e) => {handleOnChange(e.target.value , "tags")}} type="tags" name="tags" />
    </label>
    <input onClick={() => {addQuotes()}} type="submit"  />
    </form>
    </div>
  )
}

export default FormQuotes
