const api = {
  getQuotes: async (params = {}) => {
    if (!token) return [];

    let paramsStr = "";
    Object.keys(params).forEach((key) => {
      paramsStr = paramsStr + key + "=" + params[key]?.join(",") + "&";
    });

    const res = await axios.get(
      `http://localhost:8000/quotes${paramsStr && "?" + paramsStr}`,
      {
        headers: { Authorization: "Bearer " + token },
      }
    );

    return res.quotes;
  },
};

export default api;
