import axios from "axios";
import React, { useState } from "react";

function FormQuotes() {
  const addQuotes = () => {
    setQuotesObj({ ...quotesObj, tags:[quotesObj.tags] });
    fetch("http://localhost:8000/quotes", {
      // Enter your IP address here

      method: "POST",
      //   mode: 'cors',
      body: JSON.stringify(quotesObj), // body data type must match "Content-Type" header
    });
  };

  const [quotesObj, setQuotesObj] = useState({
    content: "",
    author: "",
    tags: [],
  });

  const handleOnChange = (value, inputType) => {
    setQuotesObj({ ...quotesObj, [inputType]: value });
  };

  return (
    <div className="form-container">
      <form>
        <label>
          content:
          <input
            onChange={(e) => {
              handleOnChange(e.target.value, "content");
            }}
            type="content"
            name="content"
          />
        </label>
        <label>
          author:
          <input
            onChange={(e) => {
              handleOnChange(e.target.value, "author");
            }}
            type="author"
            name="author"
          />
        </label>
        <label>
          tags:
          <input
            onChange={(e) => {
              handleOnChange(e.target.value, "tags");
            }}
            type="tags"
            name="tags"
          />
        </label>
        <input
          onClick={() => {
            addQuotes();
          }}
          value="Submit"
        />
      </form>
    </div>
  );
}

export default FormQuotes;
